    [[require "model.graph"

print"拖动滑动条"

function Main(a)
  data={
    Curve(function(t)
      return Vector(3,3)+Vector(sin(2*t+a),cos(a*t))
    end,{0,10*pi}),

    Curve(function(t)
      return Vector((16*sin(t)^3)*0.2,(13 * cos(t) - 5 * cos(2*t) - 2 * cos(3*t) - cos(4*t))*0.2)
    end,{0,2*pi}),

    Curve(function(t)
      return Vector(-3,3)+Vector(cos(t),sin(t))*sin(a*t)
    end,{0,10*pi}),

    cos,

    function(x)
      return x/(sin(x)+2)
    end,

    Conic0(Vector(1,4),Vector(2,3),Vector(0,1))

  }

  graph.setData(data)

end
Main(0)
var_a.addOnChangeListener({
  onValueChange=function(view,value,bool)
    a=tonumber(value)
    Main(a)
  end
})]]
